import { Card, Image, Typography, Button, Space } from "antd";
import { useNavigate } from "react-router-dom";
import { DeleteOutlined, EditOutlined } from "@ant-design/icons";
import { useTrips } from "../hooks/useTrips";
import toast from "react-hot-toast";
import { useState } from "react";
import EditTripModal from "../modal/EditTripModal";

export default function CardComp({ trip, settingsMode, style }) {
  const navigate = useNavigate();
  const { deleteTrip } = useTrips((state) => state);
  const [editVisible, setEditVisible] = useState(false);

  const handleCardClick = (tripId) => {
    if (!settingsMode) {
      navigate(`/trip/${tripId}`);
    }
  };

  const handleDelete = async () => {
    try {
      await deleteTrip(trip.id);
      toast.success("Trip deleted");
    } catch {
      toast.error("Failed to delete trip");
    }
  };

  return (
    <>
      <Card
        hoverable={!settingsMode}
        style={{
          width: 240,
          height: 360,
          position: "relative",
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          transition: "all 0.3s ease",
          ...style,
        }}
        cover={
          <Image
            alt={trip.title}
            src={trip.image}
            height={160}
            style={{
              objectFit: "cover",
              cursor: settingsMode ? "default" : "pointer",
              borderTopLeftRadius: 12,
              borderTopRightRadius: 12,
            }}
            fallback="data:image/png;base64,"
            preview={false}
            onClick={() => handleCardClick(trip.id)}
          />
        }
      >
        <Card.Meta
          title={
            <Typography.Text
              ellipsis={{ tooltip: trip.title }}
              style={{ fontSize: 16, fontWeight: 600 }}
            >
              {trip.title}
            </Typography.Text>
          }
          description={
            <Typography.Paragraph
              ellipsis={{ rows: 3, tooltip: trip.description }}
              type="secondary"
              style={{ margin: 0, fontSize: 14 }}
            >
              {trip.description}
            </Typography.Paragraph>
          }
        />

        {settingsMode && (
          <Space
            style={{ marginTop: 12, display: "flex", justifyContent: "center" }}
          >
            <Button
              icon={<EditOutlined />}
              type="default"
              size="small"
              onClick={() => setEditVisible(true)}
              style={{ borderRadius: 6 }}
            >
              Edit
            </Button>
            <Button
              icon={<DeleteOutlined />}
              type="primary"
              danger
              size="small"
              onClick={handleDelete}
              style={{ borderRadius: 6 }}
            >
              Delete
            </Button>
          </Space>
        )}
      </Card>

      {editVisible && (
        <EditTripModal
          open={editVisible}
          onClose={() => setEditVisible(false)}
          trip={trip}
        />
      )}
    </>
  );
}
